public class Part2 {

    public float cgRatio(String dna){
        int noOfC=0,noOfG=0;
        for(int i=0; i<dna.length(); i++){
            if(dna.charAt(i) == 'C')
                noOfC++;
            if(dna.charAt(i) == 'G')
                noOfG++;
        }
        return (float)noOfC/noOfG;
    }

    public int countCTG(String dna){
        int noOfTimes=0;
        int x = dna.indexOf("CTG",0);
        while(true){
            if(x==-1)
                return noOfTimes;
            else
                noOfTimes++;
            x = dna.indexOf("CTG",x+3);           
        }
    }
    
    public void check(){
    System.out.println(countCTG(""));   
    }

}
